// Importar el módulo mysql
const mysql = require('mysql');

// Configurar la conexión a la base de datos
const conexion = mysql.createConnection({
    host: 'localhost',       // Cambiar si tu base de datos está en otro servidor
    user: 'tu_usuario',      // Reemplaza con tu usuario de MySQL
    password: 'tu_contraseña', // Reemplaza con tu contraseña de MySQL
    database: 'ejemplo_db'   // Base de datos a la que te conectarás
});

// Conectar a la base de datos
conexion.connect((err) => {
    if (err) {
        console.error('Error al conectar a la base de datos:', err);
        return;
    }
    console.log('Conexión exitosa a la base de datos.');
});

// Exportar la conexión para usarla en otros módulos si es necesario
module.exports = conexion;
